import { useState } from "react";
import { 
  BrowserRouter,
  Routes,
  Route,
  useNavigate,
  useLocation,
  Link, 
  Navigate
} from "react-router-dom";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  getAuth,
  signOut,
} from "firebase/auth";

/* ---------------------------------------------------------- */

export default function Dashboard() {
    let navigate = useNavigate();

    const [registerEmail, setRegisterEmail] = useState("");
    const [registerPassword, setRegisterPassword] = useState("");

    const [user, setUser] = useState({})

    const auth = getAuth();

    /*
    If the user is NOT already logged in and navigates to this 
    page, redirect to landing page
    */
    onAuthStateChanged(auth, (currentUser) => {
        setUser(currentUser)
        if (!currentUser) {
            navigate('/');
            const uid = currentUser.uid;
        }
    });

    /*
    Logs out and navigates to the landing page
    */
    const logout = async () => {
        await signOut(auth);
        navigate('/');
    }

    /*
    Navigates to the Rooms page
    */
    const rooms = async () => {
        navigate('/Rooms');
    }

    return (
        <div>
            <h1>DASHBOARD</h1>
            <h3>User Logged In:{user?.email}</h3>
            <button onClick={logout}>Log out</button>
            <button onClick={rooms}>Rooms</button>
        </div>
    )
}
