import GameTile from "./GameTile";
function IsVictory(G, ctx) {
  return G.gameTurns === ctx.turn;
}

const BreadsticksGame = {
  name: "Breadsticks-Game",

  setup: (ctx, setupData) => ({
    cells: Array(10).fill(GameTile),
    scores: Array(4).fill(0),
    dieRoll: 0,
    gameTurns : 10
  }),

  moves: {
    clickCell(G, ctx, id) {
      if (id === 10) {
        G.dieRoll = ctx.random.D6();
      }
      else if (G.cells[id] === null) {
        G.cells[id].playerOn = ctx.currentPlayer;
        G.cells[id].hasStar = false;
      }
    }
  },

  turn: { moveLimit: 1 },

   // The minimum and maximum number of players supported
  // (This is only enforced when using the Lobby server component.)
  minPlayers: 1,
  maxPlayers: 4,

  endIf: (G, ctx) => {
    if (IsVictory(G, ctx)) {
      return { winner: ctx.currentPlayer };
    }
  },

  ai: {
    enumerate: G => {
      let moves = [];
      for (let i = 0; i < 9; i++) {
        if (G.cells[i].playerOn === null) {
          moves.push({ move: "clickCell", args: [i] });
        }
      }
      return moves;
    }
  }
};

export default BreadsticksGame;
