import React, { useEffect, useState } from "react";
import PropTypes from 'prop-types'
import Chat from './Chat'
import { 
  useNavigate,
} from "react-router-dom";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  getAuth,
  signOut,
} from "firebase/auth";
import io from "socket.io-client";

/* ---------------------------------------------------------- */

const socket = io.connect("http://localhost:3001");

const Rooms = () => {

  const auth = getAuth();
  let navigate = useNavigate();

  const [user, setUser] = useState({});
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [showChat, setShowChat] = useState(false);
  const [userList, setUserList] = useState([]);

  onAuthStateChanged(auth, (currentUser) => {
    if (currentUser) {
        setUser(currentUser)
        var str = user?.email;
        str = str.substring(0, str.indexOf('@'));
        setUsername(str)
    }
    else {
      navigate('/');
    }
  });

  const back = async () => {
    navigate('/Dashboard');
  }

  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit("join_room", {username, room});
      // setUserList((list) => [...list, username]);
      setShowChat(true);
    }
  };

  /* adds user to ist of users */
  useEffect(() => {
    socket.on("add_user", username => {
      setUserList((list) => [...list, username]);
    });
  }, [socket]);

  /* removes user from list of users */
  useEffect(() => {
    socket.on("remove_user", username => {
      setUserList((list) => list.filter((item) => item !== username));
    });
    var room = io.sockets.adapter.rooms['my_room'];
    console.log(room.length);
  }, [socket]);

  return (
    <div>
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Join A Room, {username}</h3>
          <input
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
          <button type='submit' onClick={joinRoom}>Join</button>
          <button onClick={back}> Back to Dashboard </button> 
        </div>

      ) : (
        <div>
          <Chat socket={socket} username={username} room={room} setShowChat={setShowChat} />
          <h3>Current Users: {userList}</h3>
        </div>
      )}
    </div>
  )
}

Rooms.defaultProps = {
  userName: 'User123',
}

export default Rooms
